#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
# Support pipeline input
if ($MyInvocation.ExpectingInput) {
  $input | & "D:/projects/auto-agent/llm-api/node_modules/node-llama-cpp/llama/xpack/store/@xpack-dev-tools/cmake/3.27.9-1.2/.content/bin/cpack.exe"   $args
} else {
  & "D:/projects/auto-agent/llm-api/node_modules/node-llama-cpp/llama/xpack/store/@xpack-dev-tools/cmake/3.27.9-1.2/.content/bin/cpack.exe"   $args
}
exit $LASTEXITCODE
